import React from 'react';
import './App.css';

/*
function Helloworld (props) {
  return (
    <div id="hello">
      <h3>{props.subtitle}</h3>
      {props.mytext}</div>
  )
}
*/

class Helloworld extends React.Component {
  state = {
    show: true
  }

  render() {
    if(this.state.show) {
      return (
        <div id="hello">
          <h3>{this.props.subtitle}</h3>
          {this.props.mytext}
          <button onClick={() => this.state.show=false}>Toggle Show</button>
          </div>          
      )
    }
    else {
      return (<h3>Empty</h3>)
    }
  }
}

function App() {
  return (
    <div>
      This is my component:
      <Helloworld mytext="Hello mate 1" subtitle="cosa 1"/>
      <Helloworld mytext="Hello mate 2" subtitle="cosa 2"/>
      <Helloworld mytext="Hello mate 3" subtitle="cosa 3"/></div>
  );
}

export default App;
